import React, { useState, useEffect } from "react";

export default function Timer() {
  const [partyTime, setPartyTime] = useState(false);
  const [days, setDays] = useState(0);
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    // const target = new Date("02/02/2022 5:00:00 PM UTC");
    const target = new Date("02/02/2022 5:00:00 PM UTC");

    const interval = setInterval(() => {
      const now = new Date();
      const difference = target.getTime() - now.getTime();

      const d = Math.floor(difference / (1000 * 60 * 60 * 24));
      setDays(d);

      const h = Math.floor(
        (difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
      );
      setHours(h);

      const m = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60));
      setMinutes(m);

      const s = Math.floor((difference % (1000 * 60)) / 1000);
      setSeconds(s);

      if (d <= 0 && h <= 0 && m <= 0 && s <= 0) {
        setPartyTime(true);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      {!partyTime ? (
        <>
          {/* <div className="mt-10">
            <div className="flex   text-center justify-center text-2xl lg:text-3xl semibold">
              <div className="font-semibold ">
                <span className="time">{days} Days</span>
              </div>
              <span className="divider">:</span>
              <div className="font-semibold ">
                <span className="time">{hours} Hours</span>
              </div>
              <span className="divider">:</span>
              <div className="font-semibold">
                <span className="time">{minutes} Minutes</span>
              </div>
              <span className="divider">:</span>
              <div className="font-semibold">
                <span className="time">{seconds} Seconds</span>
              </div>
            </div>
          </div> */}
          <p className="claim">
            {days} : {hours} : {minutes} : {seconds}
          </p>
        </>
      ) : null}
    </div>
  );
}
