import React, { useState } from "react";
import drone from "../assests/001.gif";
import { ethers } from "ethers";
import ABI from "../Blockchain/ABI.json";
import { useAlert } from "react-alert";

const nftaddress = "0x37ec1FA103998795b17017aA0B889F0a43477009";
const Minting = () => {
  const alert = useAlert();

  const [mintComplete, setMintComplete] = useState(false);
  const [address, setaddress] = useState(null);
  const [isloading, setIsloading] = useState(false);
  const [status, setStatus] = useState("init");
  const [num, setNum] = useState(0);

  const connectWallet = async () => {
    // window.ethereum.request
    if (!window.ethereum || !window.ethereum.isMetaMask) {
      console.log("No metamask");
      alert.error("Please use a browser with Metamask");
      return;
    }
    //check chain id
    const chainId = await window.ethereum.request({ method: "eth_chainId" });
    if (chainId !== "0x1") {
      console.log("Wrogn Chain");
      alert.error("Please use Etherium Mainnet ");
      return;
    }
    try {
      const provider = new ethers.providers.Web3Provider(
        window.ethereum,
        "any"
      );
      // Prompt user for account connections
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();

      let address = await signer.getAddress();
      console.log("ADDRESS", address);
      setaddress(address);
      setStatus("connected");
    } catch (err) {
      console.log(err);
      if (err.message.includes("unknown account")) {
        alert.error("Please connect your Metamask Wallet!");
      } else alert.error("Sorry, An Error Occured!");
      //   setShowMintedModal(false);
    }
  };

  const mint = async () => {
    // window.ethereum.request
    if (!window.ethereum || !window.ethereum.isMetaMask) {
      console.log("No metamask");
      alert.error("Please use a browser with Metamask");
      return;
    }
    //check chain id
    const chainId = await window.ethereum.request({ method: "eth_chainId" });
    if (chainId !== "0x1") {
      console.log("Wrogn Chain");
      alert.error("Please use Etherium Mainnet ");
      return;
    }
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    // const provider = new ethers.providers.getDefaultProvider("inkeby", {
    //   infura: "https://mainnet.infura.io/v3/44c345ec9cac43e3a461dbfffe965e5d",
    // });

    try {
      // console.log(await signer.getAddress());
      const signer = provider.getSigner();
      // setaddress(await signer.getAddress());

      let contract = new ethers.Contract(nftaddress, ABI, signer);
      // console.log(contract);
      let address = await signer.getAddress();
      // console.log("ADDRESS", address);
      setaddress(address);
      //infura
      //   setShowModal(false);
      //   setMintedNum(0);
      //   setShowMintedModal(true);
      //   setIsloading(true);
      //   let priceinnum = 0.08 * num;
      //   let priceInWei = ethers.utils.parseEther(priceinnum.toString());
      // console.log(ethers.utils.parseUnits("1000000000000000", "priceInWei"));
      // console.log(wei, priceinnum.toString());
      // return;
      // let price = ethers.utils.parseUnits("1000000000000000", "wei");
      //estimate gas
      //   let gas = await contract.estimateGas.mint(num, {
      //     value: priceInWei,
      //   });
      //   gas = gas.mul(3);
      //   gas = gas.div(2);
      //   // console.log(gas.toNumber());
      //   // return;
      //   //transaction
      //   let transaction = await contract.mint(num, {
      //     value: priceInWei,
      //     gasLimit: gas,
      //   });
      //   await transaction.wait();
      // console.log(transaction);

      // console.log(monstersarray);
      setIsloading(false);
    } catch (err) {
      console.log(err);
      if (err.message.includes("unknown account")) {
        alert.error("Please connect your Metamask Wallet!");
      } else if (err.message.includes("Exceeds MAX_Supply")) {
        alert.error("Payment Failed,exeeded max supply!");
      } else if (err.message.includes("insufficient funds")) {
        alert.error("Payment Failed,Insufficient funds in your account!");
      } else if (err.message.includes("not on the White List")) {
        alert.error("Payment Failed,Your account is not on the Whitelist!");
      } else alert.error("Payment Failed, please try again!");
      //   setShowMintedModal(false);
    }
  };

  const InitComponent = () => {
    return (
      <>
        <div className="drone-bt-icon">
          <img src={drone} alt="drone" />
        </div>
        <p className="claim">CLAIM IS LIVE</p>
        <button onClick={connectWallet} className="connect">
          CONNECT METAMASK
        </button>
      </>
    );
  };

  const ClaimComponent = () => {
    return (
      <>
        <div className="drone-bt-icon">
          <img src={drone} alt="drone" />
        </div>
        <p className="claim">X CLAIMS LEFT WITH YOUR WALLET</p>
        <br />
        <p className="claim" id="address-text">
          Connected With {address.slice(0, 4)}...{address.slice(-3)}
        </p>
        <p onClick={connectWallet} className="mint-num">
          MINT + {num} - DRONES
        </p>
        <button onClick={connectWallet} className="connect">
          MINT DRONES
        </button>
      </>
    );
  };

  return (
    <section className="mint-col">
      {mintComplete ? (
        <>
          <p className="claim" style={{ color: "#fff" }}>
            YOU HAVE CLAIMED ALL YOUR DRONE
          </p>
          <p className="claim" style={{ marginTop: "25px" }}>
            <a
              target="_blank"
              href="https://opensea.io/collection/afrodroids-by-owo"
            >
              VIEW COLLECTION ON OPENSEA{" "}
            </a>
          </p>
        </>
      ) : status == "init" ? (
        InitComponent()
      ) : status == "connected" ? (
        ClaimComponent()
      ) : null}
    </section>
  );
};

export default Minting;
